package ru.kpfu.itis.lzakharov.contacts.rest.model;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;@Entitypublic class User {    @Id    private String login;    @Column(unique = true)    private String fullName;    public String getLogin() {        return login;    }    public void setLogin(String login) {        this.login = login;    }    public String getFullName() {        return fullName;    }    public void setFullName(String fullName) {        this.fullName = fullName;    }}