package ru.kpfu.itis.lzakharov.contacts.model;import org.hibernate.validator.constraints.NotBlank;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;@Entitypublic class User {    @Id    @NotBlank    private String login;    @Column(nullable = false)    private String fullName;    private String address;    public String getLogin() {        return login;    }    public void setLogin(String login) {        this.login = login;    }    public String getFullName() {        return fullName;    }    public void setFullName(String fullName) {        this.fullName = fullName;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }}