package ru.kpfu.itis.lzakharov.contacts.service.impl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ru.kpfu.itis.lzakharov.contacts.model.User;import ru.kpfu.itis.lzakharov.contacts.repository.UserRepository;import ru.kpfu.itis.lzakharov.contacts.service.UserService;import java.util.List;@Servicepublic class UserServiceImpl implements UserService {    @Autowired    UserRepository userRepository;    @Override    public List<User> findAll() {        return (List<User>) userRepository.findAll();    }    @Override    public User find(String login) {        return userRepository.findOne(login);    }    @Override    public boolean isUserExist(User user) {        return find(user.getLogin()) != null;    }    @Override    public User create(User user) {        return userRepository.save(user);    }    @Override    public User update(User user) {        return userRepository.save(user);    }    @Override    public void delete(String login) {        userRepository.delete(login);    }}